import React, { Component } from 'react';

class JSXInfo extends Component {
    render(){
        return (
            <div className="jsx-info">
                <h2>JSX</h2>
                <p>The first part of a JSX tag determines the type of the React element. Capitalized types indicate that the JSX tag is referring to a React component.</p>
            </div>
        )
    }
}

export default JSXInfo;